  'id',
  'name',
  'identification',
  'type',
  'email',
  'phone',
  'image_url',
  'web_url',
  'country',
  'addressLine1',
  'addressLine2',
  'metadata',
  'settings',
  'updated_by',
  'created_by',
  'created_at',
  'updated_at'
];

const USERS_BY_ORGANIZER_TABLE_COLUMNS = [
  'id',
  'external_user_id',
  'event_organizer_id',
  'updated_by',
  'created_by',
  'created_at',
  'updated_at'
];

export const getEventOrganizerById = async (dbContext, organizerId) => { 
  const unitOfWork = new UnitOfWork(dbContext);
